package entrada_processamento_de_dados;



public class ex24w {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a, b;
		 a = 1;
     while (a<=10) {
      b=a*5;
      System.out.printf("\n%d = %d x 5",b,a);
      b =a+5;
      System.out.printf("\n%d = %d + 5",b,a);
      b=a-5;
      System.out.printf("\n%d = %d - 5",b,a);
      b=a/5;
      System.out.printf("\n%d = %d / 5",b,a);
      
     a++;
     }
     }
}
